  	extend: {
  		borderRadius: {
  			lg: 'var(--radius)',
  			md: 'calc(var(--radius) - 2px)',
  			sm: 'calc(var(--radius) - 4px)'
  		},
  		colors: {
  			background: 'hsl(var(--background))',
  			foreground: 'hsl(var(--foreground))',
  			card: {
  				DEFAULT: 'hsl(var(--card))',
  				foreground: 'hsl(var(--card-foreground))'
  			},
  			popover: {
  				DEFAULT: 'hsl(var(--popover))',
  				foreground: 'hsl(var(--popover-foreground))'
  			},
  			primary: {
  				DEFAULT: 'hsl(var(--primary))',
  				foreground: 'hsl(var(--primary-foreground))'
  			},
  			secondary: {
  				DEFAULT: 'hsl(var(--secondary))',
  				foreground: 'hsl(var(--secondary-foreground))'
  			},
  			muted: {
  				DEFAULT: 'hsl(var(--muted))',
  				foreground: 'hsl(var(--muted-foreground))'
  			},
  			accent: {
  				DEFAULT: 'hsl(var(--accent))',
  				foreground: 'hsl(var(--accent-foreground))'
  			},
  			destructive: {
  				DEFAULT: 'hsl(var(--destructive))',
  				foreground: 'hsl(var(--destructive-foreground))'
  			},
  			border: 'hsl(var(--border))',
  			input: 'hsl(var(--input))',
  			ring: 'hsl(var(--ring))',
  			chart: {
  				'1': 'hsl(var(--chart-1))',
  				'2': 'hsl(var(--chart-2))',
  				'3': 'hsl(var(--chart-3))',
  				'4': 'hsl(var(--chart-4))',
  				'5': 'hsl(var(--chart-5))'
  			}
  		},
  		keyframes: {
  			'accordion-down': {
  				from: {
  					height: '0'
  				},
  				to: {
  					height: 'var(--radix-accordion-content-height)'
  				}
  			},
  			'accordion-up': {
  				from: {
  					height: 'var(--radix-accordion-content-height)'
  				},
  				to: {
  					height: '0'
  				}
  			}
  		},
  		animation: {
  			'accordion-down': 'accordion-down 0.2s ease-out',
  			'accordion-up': 'accordion-up 0.2s ease-out'
  		}
  	}
  },
  plugins: [require("tailwindcss-animate")],
};

frontend/src/App.css.content:
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800&family=JetBrains+Mono:wght@400;500;600&display=swap');

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: 'Inter', sans-serif;
  background: #020617;
  color: #f1f5f9;
  line-height: 1.6;
  overflow-x: hidden;
}

.App {
  min-height: 100vh;
}

/* Landing Page Styles */
.landing-hero {
  background: linear-gradient(135deg, #0f172a 0%, #1e293b 50%, #334155 100%);
  min-height: 100vh;
  position: relative;
  overflow: hidden;
}

.landing-hero::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: 
    radial-gradient(circle at 20% 50%, rgba(14, 165, 233, 0.15) 0%, transparent 50%),
    radial-gradient(circle at 80% 20%, rgba(139, 92, 246, 0.15) 0%, transparent 50%),
    radial-gradient(circle at 40% 80%, rgba(34, 197, 94, 0.1) 0%, transparent 50%);
  pointer-events: none;
}

.hero-title {
  background: linear-gradient(135deg, #0ea5e9 0%, #8b5cf6 50%, #22c55e 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  font-size: 4.5rem;
  font-weight: 800;
  line-height: 1.1;
  text-align: center;
  margin-bottom: 1.5rem;
}

.hero-subtitle {
  font-size: 1.5rem;
  color: #94a3b8;
  text-align: center;
  margin-bottom: 3rem;
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
}

.feature-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
  margin: 4rem 0;
}

.feature-card {
  background: rgba(30, 41, 59, 0.5);
  backdrop-filter: blur(12px);
  border: 1px solid rgba(51, 65, 85, 0.5);
  border-radius: 16px;
  padding: 2rem;
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;
}

.feature-card::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 2px;
  background: linear-gradient(90deg, #0ea5e9, #8b5cf6, #22c55e);
  transform: scaleX(0);
  transition: transform 0.3s ease;
}

.feature-card:hover::before {
  transform: scaleX(1);
}

.feature-card:hover {
  transform: translateY(-4px);
  border-color: rgba(14, 165, 233, 0.3);
  box-shadow: 0 20px 40px rgba(14, 165, 233, 0.1);
}

.feature-icon {
  width: 48px;
  height: 48px;
  margin-bottom: 1rem;
  color: #0ea5e9;
}

.feature-title {
  font-size: 1.25rem;
  font-weight: 600;
  color: #f1f5f9;
  margin-bottom: 0.5rem;
}

.feature-description {
  color: #94a3b8;
  line-height: 1.6;
}

/* Game Card Styles */
.game-card {
  background: rgba(30, 41, 59, 0.6);
  backdrop-filter: blur(16px);
  border: 1px solid rgba(51, 65, 85, 0.4);
  border-radius: 20px;
  padding: 2rem;
  transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  position: relative;
  overflow: hidden;
  cursor: pointer;
}

.game-card::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(135deg, 
    rgba(14, 165, 233, 0.1) 0%, 
    rgba(139, 92, 246, 0.1) 50%, 
    rgba(34, 197, 94, 0.1) 100%);
  opacity: 0;
  transition: opacity 0.4s ease;
}

.game-card:hover::before {
  opacity: 1;
}

.game-card:hover {
  transform: translateY(-8px) scale(1.02);
  border-color: rgba(14, 165, 233, 0.4);
  box-shadow: 0 25px 50px rgba(14, 165, 233, 0.15);
}

.game-icon {
  width: 64px;
  height: 64px;
  margin-bottom: 1.5rem;
  color: #0ea5e9;
  z-index: 1;
  position: relative;
}

.game-title {
  font-size: 1.5rem;
  font-weight: 700;
  color: #f1f5f9;
  margin-bottom: 0.75rem;
  z-index: 1;
  position: relative;
}

.game-description {
  color: #cbd5e1;
  margin-bottom: 1.5rem;
  z-index: 1;
  position: relative;
}

.game-stats {
  display: flex;
  justify-content: space-between;
  align-items: center;
  z-index: 1;
  position: relative;
}

.game-difficulty {
  padding: 0.25rem 0.75rem;
  background: rgba(14, 165, 233, 0.2);
  border: 1px solid rgba(14, 165, 233, 0.3);
  border-radius: 999px;
  font-size: 0.875rem;
  color: #0ea5e9;
  font-weight: 500;
}

.game-play-btn {
  background: linear-gradient(135deg, #0ea5e9 0%, #8b5cf6 100%);
  color: white;
  border: none;
  padding: 0.75rem 1.5rem;
  border-radius: 12px;
  font-weight: 600;
  font-size: 0.9rem;
  cursor: pointer;
  transition: all 0.3s ease;
  text-decoration: none;
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
}

.game-play-btn:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 16px rgba(14, 165, 233, 0.3);
}

/* Navigation Styles */
.navbar {
  background: rgba(15, 23, 42, 0.9);
  backdrop-filter: blur(12px);
  border-bottom: 1px solid rgba(51, 65, 85, 0.3);
  padding: 1rem 0;
  position: sticky;
  top: 0;
  z-index: 50;
}

.nav-content {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.nav-brand {
  font-size: 1.5rem;
  font-weight: 700;
  background: linear-gradient(135deg, #0ea5e9, #8b5cf6);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  text-decoration: none;
}

.nav-links {
  display: flex;
  gap: 2rem;
  align-items: center;
}

.nav-link {
  color: #cbd5e1;
  text-decoration: none;
  font-weight: 500;
  transition: color 0.3s ease;
  padding: 0.5rem 1rem;
  border-radius: 8px;
}

.nav-link:hover {
  color: #0ea5e9;
  background: rgba(14, 165, 233, 0.1);
}

.nav-link.active {
  color: #0ea5e9;
  background: rgba(14, 165, 233, 0.1);
}

/* Form Styles */
.auth-container {
  min-height: 100vh;
  background: linear-gradient(135deg, #0f172a 0%, #1e293b 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 2rem;
}

.auth-card {
  background: rgba(30, 41, 59, 0.8);
  backdrop-filter: blur(16px);
  border: 1px solid rgba(51, 65, 85, 0.3);
  border-radius: 20px;
  padding: 3rem;
  width: 100%;
  max-width: 400px;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
}

.auth-title {
  font-size: 2rem;
  font-weight: 700;
  text-align: center;
  margin-bottom: 2rem;
  background: linear-gradient(135deg, #0ea5e9, #8b5cf6);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.form-group {
  margin-bottom: 1.5rem;
}

.form-label {
  display: block;
  margin-bottom: 0.5rem;
  font-weight: 500;
  color: #e2e8f0;
}

.form-input {
  width: 100%;
  padding: 0.875rem 1rem;
  background: rgba(15, 23, 42, 0.8);
  border: 1px solid rgba(51, 65, 85, 0.5);
  border-radius: 12px;
  color: #f1f5f9;
  font-size: 1rem;
  transition: all 0.3s ease;
}

.form-input:focus {
  outline: none;
  border-color: #0ea5e9;
  box-shadow: 0 0 0 3px rgba(14, 165, 233, 0.1);
}

.form-input::placeholder {
  color: #64748b;
}

.btn-primary {
  width: 100%;
  background: linear-gradient(135deg, #0ea5e9 0%, #8b5cf6 100%);
  color: white;
  border: none;
  padding: 1rem;
  border-radius: 12px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  margin-bottom: 1rem;
}

.btn-primary:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 16px rgba(14, 165, 233, 0.3);
}

.btn-primary:disabled {
  opacity: 0.6;
  cursor: not-allowed;
  transform: none;
}

.auth-link {
  text-align: center;
  color: #94a3b8;
}

.auth-link a {
  color: #0ea5e9;
  text-decoration: none;
  font-weight: 500;
}

.auth-link a:hover {
  text-decoration: underline;
}

/* Game Styles */
.game-container {
  min-height: 100vh;
  background: linear-gradient(135deg, #0f172a 0%, #1e293b 100%);
  padding: 2rem;
}

.game-content {
  max-width: 1000px;
  margin: 0 auto;
}

.game-header {
  text-align: center;
  margin-bottom: 3rem;
}

.game-title-main {
  font-size: 2.5rem;
  font-weight: 700;
  background: linear-gradient(135deg, #0ea5e9, #8b5cf6);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  margin-bottom: 1rem;
}

.game-subtitle {
  color: #94a3b8;
  font-size: 1.1rem;
}

.game-area {
  background: rgba(30, 41, 59, 0.6);
  backdrop-filter: blur(16px);
  border: 1px solid rgba(51, 65, 85, 0.4);
  border-radius: 20px;
  padding: 2rem;
  margin-bottom: 2rem;
}

.game-image {
  width: 100%;
  max-width: 400px;
  height: 300px;
  object-fit: cover;
  border-radius: 12px;
  margin: 0 auto 2rem;
  display: block;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.3);
}

.game-options {
  display: flex;
  gap: 1rem;
  justify-content: center;
  flex-wrap: wrap;
}

.game-option-btn {
  background: rgba(15, 23, 42, 0.8);
  border: 1px solid rgba(51, 65, 85, 0.5);
  color: #f1f5f9;
  padding: 1rem 2rem;
  border-radius: 12px;
  font-size: 1rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.3s ease;
  min-width: 120px;
}

.game-option-btn:hover {
  border-color: #0ea5e9;
  background: rgba(14, 165, 233, 0.1);
  transform: translateY(-2px);
}

.game-option-btn.correct {
  background: rgba(34, 197, 94, 0.2);
  border-color: #22c55e;
  color: #22c55e;
}

.game-option-btn.incorrect {
  background: rgba(239, 68, 68, 0.2);
  border-color: #ef4444;
  color: #ef4444;
}

.game-stats {
  display: flex;
  justify-content: space-between;
  align-items: center;
  background: rgba(15, 23, 42, 0.8);
  padding: 1rem 2rem;
  border-radius: 12px;
  margin-bottom: 2rem;
}

.stat-item {
  text-align: center;
}

.stat-label {
  color: #94a3b8;
  font-size: 0.875rem;
  margin-bottom: 0.25rem;
}

.stat-value {
  font-size: 1.25rem;
  font-weight: 600;
  color: #0ea5e9;
}

.game-progress {
  text-align: center;
  margin-bottom: 2rem;
}

.progress-bar {
  width: 100%;
  height: 8px;
  background: rgba(51, 65, 85, 0.5);
  border-radius: 4px;
  overflow: hidden;
  margin-bottom: 1rem;
}

.progress-fill {
  height: 100%;
  background: linear-gradient(90deg, #0ea5e9, #8b5cf6);
  transition: width 0.3s ease;
}

/* Memory Game Specific */
.memory-sequence {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 1rem;
  max-width: 300px;
  margin: 2rem auto;
}

.memory-tile {
  aspect-ratio: 1;
  background: rgba(15, 23, 42, 0.8);
  border: 2px solid rgba(51, 65, 85, 0.5);
  border-radius: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 2rem;
  font-weight: 700;
  color: #94a3b8;
  cursor: pointer;
  transition: all 0.3s ease;
}

.memory-tile.active {
  background: linear-gradient(135deg, #0ea5e9, #8b5cf6);
  color: white;
  transform: scale(1.05);
}

.memory-tile.user-input {
  border-color: #22c55e;
  background: rgba(34, 197, 94, 0.2);
}

.memory-tile.incorrect {
  border-color: #ef4444;
  background: rgba(239, 68, 68, 0.2);
  animation: shake 0.5s ease-in-out;
}

@keyframes shake {
  0%, 100% { transform: translateX(0); }
  25% { transform: translateX(-5px); }
  75% { transform: translateX(5px); }
}

/* Results Screen */
.results-screen {
  text-align: center;
  padding: 3rem;
  background: rgba(30, 41, 59, 0.6);
  backdrop-filter: blur(16px);
  border: 1px solid rgba(51, 65, 85, 0.4);
  border-radius: 20px;
  margin: 2rem auto;
  max-width: 600px;
}

.results-title {
  font-size: 2rem;
  font-weight: 700;
  margin-bottom: 2rem;
  background: linear-gradient(135deg, #0ea5e9, #8b5cf6);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.results-comparison {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 2rem;
  margin: 2rem 0;
}

.comparison-item {
  background: rgba(15, 23, 42, 0.8);
  padding: 2rem;
  border-radius: 12px;
  border: 1px solid rgba(51, 65, 85, 0.5);
}

.comparison-label {
  color: #94a3b8;
  font-size: 0.875rem;
  margin-bottom: 0.5rem;
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

.comparison-score {
  font-size: 2rem;
  font-weight: 700;
  color: #0ea5e9;
}

.comparison-item.ai {
  .comparison-score {
    color: #8b5cf6;
  }
}

/* Leaderboard Styles */
.leaderboard-container {
  max-width: 1000px;
  margin: 0 auto;
  padding: 2rem;
}

.leaderboard-header {
  text-align: center;
  margin-bottom: 3rem;
}

.leaderboard-title {
  font-size: 2.5rem;
  font-weight: 700;
  background: linear-gradient(135deg, #0ea5e9, #8b5cf6);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  margin-bottom: 1rem;
}

.leaderboard-grid {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 2rem;
}

.leaderboard-section {
  background: rgba(30, 41, 59, 0.6);
  backdrop-filter: blur(16px);
  border: 1px solid rgba(51, 65, 85, 0.4);
  border-radius: 20px;
  padding: 2rem;
}

.section-title {
  font-size: 1.5rem;
  font-weight: 600;
  margin-bottom: 1.5rem;
  text-align: center;
}

.section-title.human {
  color: #22c55e;
}

.section-title.ai {
  color: #8b5cf6;
}

.leaderboard-list {
  list-style: none;
}

.leaderboard-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem;
  background: rgba(15, 23, 42, 0.6);
  border-radius: 12px;
  margin-bottom: 0.5rem;
  border: 1px solid rgba(51, 65, 85, 0.3);
}

.leaderboard-item.top-3 {
  border-color: #f59e0b;
  background: rgba(245, 158, 11, 0.1);
}

.leaderboard-rank {
  font-weight: 700;
  color: #f59e0b;
  margin-right: 1rem;
}

.leaderboard-name {
  flex: 1;
  font-weight: 500;
}

.leaderboard-score {
  font-weight: 600;
  color: #0ea5e9;
}

/* Responsive Design */
@media (max-width: 768px) {
  .hero-title {
    font-size: 3rem;
  }

  .hero-subtitle {
    font-size: 1.25rem;
  }

  .feature-grid {
    grid-template-columns: 1fr;
  }

  .game-options {
    flex-direction: column;
  }

  .game-option-btn {
    width: 100%;
  }

  .game-stats {
    flex-direction: column;
    gap: 1rem;
  }

  .results-comparison {
    grid-template-columns: 1fr;
  }

  .leaderboard-grid {
    grid-template-columns: 1fr;
  }

  .nav-content {
    padding: 0 1rem;
  }

  .nav-links {
    gap: 1rem;
  }
}

@media (max-width: 480px) {
  .hero-title {
    font-size: 2.5rem;
  }

  .auth-card {
    padding: 2rem;
  }

  .game-container {
    padding: 1rem;
  }

  .game-area {
    padding: 1.5rem;
  }
}

frontend/src/App.js.content:
import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import axios from 'axios';
import './App.css';

// Components
import LandingPage from './components/LandingPage';
import Dashboard from './components/Dashboard';
import GameAIImage from './components/games/GameAIImage';
import GameTextAI from './components/games/GameTextAI';
import GameMemory from './components/games/GameMemory';
import GameLogicalReasoning from './components/games/GameLogicalReasoning';
import GameCreativeWriting from './components/games/GameCreativeWriting';
import GameAudioRecognition from './components/games/GameAudioRecognition';
import ContentAuthentication from './components/ContentAuthentication';
import Leaderboard from './components/Leaderboard';
import UserStats from './components/UserStats';

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;
const API = `${BACKEND_URL}/api`;

// Auth Context (kept for consumer components; defaults to Guest user)
const AuthContext = React.createContext();

function App() {
  const [user] = useState({ id: 'guest', username: 'Guest', total_games_played: 0, total_score: 0 });

  return (
    <AuthContext.Provider value={{ user }}>
      <div className="App">
        <BrowserRouter>
          <Routes>
            <Route path="/" element={<LandingPage />} />
            <Route path="/dashboard" element={<Dashboard />} />
            <Route path="/game/ai-image" element={<GameAIImage />} />
            <Route path="/game/text-ai" element={<GameTextAI />} />
            <Route path="/game/memory" element={<GameMemory />} />
            <Route path="/game/logical-reasoning" element={<GameLogicalReasoning />} />
            <Route path="/game/creative-writing" element={<GameCreativeWriting />} />
            <Route path="/game/audio-recognition" element={<GameAudioRecognition />} />
            <Route path="/content-authentication" element={<ContentAuthentication />} />
            <Route path="/leaderboard" element={<Leaderboard />} />
            <Route path="/stats" element={<UserStats />} />
          </Routes>
        </BrowserRouter>
      </div>
    </AuthContext.Provider>
  );
}

export { AuthContext, API };
export default App;

frontend/src/index.css.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

body {
    margin: 0;
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", "Roboto",
        "Oxygen", "Ubuntu", "Cantarell", "Fira Sans", "Droid Sans",
        "Helvetica Neue", sans-serif;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
